//Add and remove close button to the news bubble with given delay.
window.attachEventListenersCloseButton = (bubble) => {

    let closeButtonElement = document.querySelector('.newsBubble .closeButton');
    let timeOut = null;
    let delay = 1000;

    if (closeButtonElement) {
        bubble.addEventListener('mouseenter', (event) => {
            timeOut = setTimeout(() => {
                closeButtonElement.style.display = 'flex';
            }, delay)
        });

        bubble.addEventListener('mouseleave', (event) => {
            clearTimeout(timeOut);
            closeButtonElement.style.display = 'none';
        });
    }
}