/**
 * Simple js function implemented for the news overlay. It provides a smooth disappearing effect to the header when the user is scrolling.
 * @param scrollingElement - the parent element, which has a scroll
 * @param headerContainer - the header element which will receive the fade-in/out effect
 */
window.addSmoothHeader = (scrollingElement, headerContainer) => {
    scrollingElement.addEventListener('scroll', () => {
        let scroll = scrollingElement.scrollTop || 0;
        headerContainer.style.opacity = Math.max(0, Math.min(1, (100 - scroll) / 100));
        headerContainer.style.transform = 'translateY(' + Math.max(0, scroll / 1.3) + 'px)';
    });
};